sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

          // Add your code here
	var sameBirthday = new GlideRecord('x_58872_needit_occasions');
	sameBirthday.addQuery('u_occasion_month', current.occasion_month);
	sameBirthday.addQuery('u_occasion_day',current.occasion_day);
	sameBirthday.addQuery('u_occasion','birthday');

	sameBirthday.query();

	template.print(sameBirthday.getRowCount() -1 + "Employees in your company share your birthday");

})(current, template, email, email_action, event);
